DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` bigint(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_name` varchar(50) NOT NULL COMMENT '用户名',
  `password` varchar(32) NOT NULL COMMENT '密码',
  `country` int(11) DEFAULT NULL COMMENT '国家',
  `expired_date` datetime DEFAULT NULL COMMENT '过期日期',
  `credentials_expired` varchar(200) DEFAULT NULL COMMENT '过期的凭据',
  `full_name` varchar(20) DEFAULT NULL COMMENT '用户全名',
  `org` int(11) DEFAULT NULL COMMENT '用户所在机构',
  `gender` varchar(10) DEFAULT NULL COMMENT '性别(10代表男性，20代表女性)',
  `age` int(11) DEFAULT NULL COMMENT '年龄',
  `address` varchar(50) DEFAULT NULL COMMENT '地址',
  `phone` varchar(20) DEFAULT NULL COMMENT '电话',
  `mobile` varchar(20) DEFAULT NULL COMMENT '移动电话',
  `ssn` varchar(30) DEFAULT NULL COMMENT '社会安全卡',
  `mail` varchar(30) DEFAULT NULL COMMENT '邮箱地址',
  `user_type` varchar(10) DEFAULT '1' COMMENT '用户类型 10普通用户 20超级管理员',
  `notify_mode` varchar(10) DEFAULT NULL COMMENT '通知模式 1邮箱通知 2电话通知  3短信通知',
  `description` varchar(200) DEFAULT NULL COMMENT '描述',
  `creator_user_id` bigint(10) DEFAULT NULL COMMENT '创造者',
  `status` varchar(10) DEFAULT '10' COMMENT '用户状态 10能正常使用  20用户帐户锁定 30帐号已过期',
  `creation_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `rsv1` varchar(50) DEFAULT NULL COMMENT '备用字段1',
  `rsv2` varchar(50) DEFAULT NULL COMMENT '备用字段1',
  `rsv3` varchar(50) DEFAULT NULL COMMENT '备用字段1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `user_name_uq` (`user_name`)
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8;

