mapper.select:
SELECT obj_name, rec_version, tab_version FROM ${TowerTabName}
and obj_name=#{id}
limit 1

		SELECT
		obj_name,
		rec_version,
		tab_version
		FROM
		${TowerTabName}
		
			
				and obj_name=#{id}
			
			
				and rec_version=#{recVersion}
			
			
				and tab_version=#{tabVersion}

		SELECT
		obj_name
		FROM
		${TowerTabName}
		
			
				and obj_name=#{id}
			
			
				and rec_version=#{recVersion}
			
			
				and tab_version=#{tabVersion}

		SELECT
		count(*)
		FROM
		${TowerTabName}
		
			
				and obj_name=#{id}
			
			
				and rec_version=#{recVersion}
			
			
				and tab_version=#{tabVersion}

SELECT
obj_name,
rec_version,
tab_version
FROM
${TowerTabName}

	
		
			and obj_name=#{params.id}
		
		
			and rec_version=#{params.recVersion}
		
		
			and tab_version=#{params.tabVersion}
		
	

 order by ${orders} 
limit #{start},#{end}

mapper.update:
		update
		${TowerTabName}
		
			
				obj_name=#{updNewMap.id},
			
			
				rec_version=#{updNewMap.recVersion},
			
			
				tab_version=#{updNewMap.tabVersion},
			
		
		
			
				and obj_name=#{updCondMap.id}
			
			
				and rec_version=#{updCondMap.recVersion}
			
			
				and tab_version=#{updCondMap.tabVersion}

mapper.delete:
		DELETE
		FROM
		${TowerTabName}
		
			
				and obj_name=#{id}
			
			
				and rec_version=#{recVersion}
			
			
				and tab_version=#{tabVersion}
